import React from 'react';
import './Home.css';

function Home() {
  const downloadResume = () => {
    fetch('http://localhost:5000/download-resume')
      .then(res => {
        if (!res.ok) throw new Error('Network response was not ok');
        return res.blob();
      })
      .then(blob => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'resume.pdf';
        document.body.appendChild(a);
        a.click();
        a.remove();
        window.URL.revokeObjectURL(url);
      })
      .catch(err => {
        alert('Download failed: ' + err.message);
      });
  };

  return (
    <div className="page-content">
      <h1>Welcome to My Profile</h1>
      <p>Hello! I'm a passionate developer eager to showcase my projects and skills. I specialize in React, Node.js, and building full-stack applications.</p>

      <div className="skills-section">
        <h2>Skills</h2>
        <ul>
          <li>JavaScript / React</li>
          <li>Node.js / Express</li>
          <li>MongoDB / Database Management</li>
          <li>CSS / Responsive Design</li>
        </ul>
      </div>

      <div className="projects-section">
        <h2>Featured Projects</h2>
        <div className="project-item">
          <h3>Portfolio Website</h3>
          <p>A MERN stack portfolio showcasing my skills and projects.</p>
          <a href="https://github.com/yourusername/portfolio" target="_blank" rel="noopener noreferrer">View on GitHub</a>
        </div>
      </div>

      <button className="resume-button" onClick={downloadResume}>Download Resume PDF</button>

      <div className="contact-section">
        <h2>Get In Touch</h2>
        <p>You can reach me via email at <a href="mailto:your.email@example.com">your.email@example.com</a> or find me on <a href="https://linkedin.com/in/yourprofile" target="_blank" rel="noopener noreferrer">LinkedIn</a>.</p>
      </div>
    </div>
  );
}

export default Home;
