import React from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import './App.css';
import './components/Home.css';
import './components/Projects.css';
import './components/About.css';
import './components/Contact.css';


// Sidebar Component
function Sidebar() {
  return (
    <div className="sidebar">
      <h2>My Profile</h2>
      <nav>
        <NavLink to="/" end className={({ isActive }) => (isActive ? 'active' : '')}>
          Home
        </NavLink>
        <NavLink to="/projects" className={({ isActive }) => (isActive ? 'active' : '')}>
          Projects
        </NavLink>
        <NavLink to="/about" className={({ isActive }) => (isActive ? 'active' : '')}>
          About
        </NavLink>
        <NavLink to="/contact" className={({ isActive }) => (isActive ? 'active' : '')}>
          Contact
        </NavLink>
      </nav>
    </div>
  );
}

// Home page with Download Resume button
function Home() {
  const downloadResume = () => {
    fetch('http://localhost:5000/download-resume')
      .then(res => {
        if (!res.ok) throw new Error('Network response was not ok');
        return res.blob();
      })
      .then(blob => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'myresume.pdf';
        document.body.appendChild(a);
        a.click();
        a.remove();
        window.URL.revokeObjectURL(url);
      })
      .catch(err => {
        alert('Failed to download resume: ' + err.message);
      });
  };

  return (
    <div className="page-content home-section">
      <h1 className="section-title">HOM<span className="highlight">E</span></h1>
      <p className="section-subtitle">Welcome to my portfolio website</p>
      <p className="home-description">
        I'm a MERN Stack Developer passionate about building user-friendly and scalable web applications.
        Feel free to download my resume and explore my work.
      </p>
      <button className="download-button" onClick={downloadResume}>Download Resume</button>
    </div>
  );
}

function Projects() {
  return (
    <div className="page-content projects-section">
      <h1 className="section-title">PROJEC<span className="highlight">TS</span></h1>
      <p className="section-subtitle">Some of the projects I’ve worked on</p>
      <div className="project-grid">
        <div className="project-card">
          <h3>Project One</h3>
          <p>MERN-based e-commerce app with cart and payment features.</p>
        </div>
        <div className="project-card">
          <h3>Project Two</h3>
          <p>ReactJS recipe generator using OpenAI API and MongoDB for storing queries.</p>
        </div>
      </div>
    </div>
  );
}

function About() {
  return (
    <div className="page-content about-section">
      <h1 className="section-title">ABO<span className="highlight">UT</span></h1>
      <p className="section-subtitle">A brief introduction to who I am and what I do</p>
      <div className="about-content">
        <img src="/icons/profile.png" alt="Profile" className="about-img" />
        <div className="about-text">
          <p>
            Hello! I'm a MERN stack developer with a passion for building clean, user-friendly applications.
            I enjoy solving problems, learning new technologies, and creating meaningful digital experiences.
          </p>
          <p>
            Outside of coding, I love reading about AI, exploring new design tools, and visiting historical places.
          </p>
        </div>
      </div>
    </div>
  );
}

function Contact() {
  return (
    <div className="page-content contact-section">
      <h1 className="section-title">CONT<span className="highlight">ACT</span></h1>
      <p className="section-subtitle">Get in touch with me</p>
      <p>Email: email@example.com</p>
      <p>LinkedIn: linkedin.com/in/yourname</p>
    </div>
  );
}

// Main App
function App() {
  return (
    <Router>
      <div className="app-container">
        <Sidebar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/projects" element={<Projects />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
