import React from 'react';
import './Home.css';

function Home() {
  const downloadResume = () => {
    fetch('http://localhost:5000/download-resume')
      .then(res => {
        if (!res.ok) throw new Error('Network response was not ok');
        return res.blob();
      })
      .then(blob => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'resume.pdf';
        document.body.appendChild(a);
        a.click();
        a.remove();
        window.URL.revokeObjectURL(url);
      })
      .catch(err => {
        alert('Download failed: ' + err.message);
      });
  };

  return (
    <div className="home-content">
      <h1>Hi, Iâ€™m [Your Name]</h1>
      <p>Welcome to my portfolio! I'm a passionate developer skilled in React, Node.js, and full-stack web development.</p>

      <div className="skills-preview">
        <h2>Skills at a Glance</h2>
        <ul>
          <li>React & Redux</li>
          <li>Node.js & Express</li>
          <li>MongoDB & Mongoose</li>
          <li>JavaScript & ES6+</li>
          <li>CSS3 / Responsive Design</li>
        </ul>
      </div>

      <div className="projects-preview">
        <h2>Featured Projects</h2>
        <p>Check out my <a href="/projects">Projects</a> page to see more!</p>
      </div>

      <button className="resume-button" onClick={downloadResume}>
        Download Resume
      </button>
    </div>
  );
}

export default Home;
