import React from 'react';
import './About.css';

const About = () => {
  return (
    <section className="about-section" id="about">
      <h2 className="section-title">
        ABO<span className="highlight">UT</span>
      </h2>
      <p className="section-subtitle">
        A brief introduction to who I am, what I do, and what drives me.
      </p>

      <div className="about-content">
        <img src="/icons/profile.png" alt="Profile" className="about-img" />
        <div className="about-text">
          <p>
            Hello! I'm a passionate web developer with a knack for building clean, user-friendly web applications. I specialize in the MERN stack and love transforming ideas into reality with code.
          </p>
          <p>
            I enjoy learning new technologies, solving problems, and contributing to open-source. Whether it's frontend aesthetics or backend logic, I strive to create impactful and efficient digital experiences.
          </p>
          <p>
            Outside of coding, I enjoy reading about AI, exploring historical places, and experimenting with design tools like Figma.
          </p>
        </div>
      </div>
    </section>
  );
};

export default About;
