import React, { useState, useEffect } from "react";

export default function App() {
  const [activeSection, setActiveSection] = useState("home");

  useEffect(() => {
    const handleScroll = () => {
      const sections = ["home", "projects", "about", "contact"];
      const scrollPos = window.scrollY + window.innerHeight / 3;

      for (let i = sections.length - 1; i >= 0; i--) {
        const section = document.getElementById(sections[i]);
        if (section && section.offsetTop <= scrollPos) {
          setActiveSection(sections[i]);
          break;
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      <header className="header">
        <div className="logo">MyPortfolio</div>
        <nav className="nav-links">
          {["home", "projects", "about", "contact"].map((sec) => (
            <a
              key={sec}
              href={`#${sec}`}
              className={activeSection === sec ? "active" : ""}
            >
              {sec.charAt(0).toUpperCase() + sec.slice(1)}
            </a>
          ))}
        </nav>
      </header>

      <main className="page-content">
        <section id="home" className="section home-section">
          <h2>Home</h2>
          <p>Welcome to my professional portfolio. Here you'll find my projects, background, and contact info.</p>
        </section>

        <section id="projects" className="section projects-section">
          <h2>Projects</h2>
          <div className="project-list">
            <div className="project-card">
              <h3>Project One</h3>
              <p>A cool project description goes here, explaining the tech and features.</p>
              <div className="project-links">
                <a href="#">Demo</a>
                <a href="#">GitHub</a>
              </div>
            </div>

            <div className="project-card">
              <h3>Project Two</h3>
              <p>Another project description, showcasing skills and results.</p>
              <div className="project-links">
                <a href="#">Demo</a>
                <a href="#">GitHub</a>
              </div>
            </div>
          </div>
        </section>

        <section id="about" className="section about-section">
          <h2>About Me</h2>
          <p>I am a software developer passionate about building beautiful and efficient web applications.</p>
          <p>With experience in React, Node.js, and modern web technologies, I deliver high-quality code.</p>
        </section>

        <section id="contact" className="section contact-section">
          <h2>Contact Me</h2>
          <form className="contact-form" onSubmit={e => e.preventDefault()}>
            <label htmlFor="name">Name</label>
            <input id="name" type="text" placeholder="Your name" />

            <label htmlFor="email">Email</label>
            <input id="email" type="email" placeholder="Your email" />

            <label htmlFor="message">Message</label>
            <textarea id="message" rows="5" placeholder="Your message"></textarea>

            <button type="submit">Send</button>
          </form>
        </section>
      </main>

      <style>{`
        /* Reset & base styles */
        body, html, #root {
          margin: 0;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          background-color: #f5f7fa;
          color: #333;
          scroll-behavior: smooth;
        }

        .header {
          position: fixed;
          top: 0;
          width: 100%;
          background-color: #1e90ff;
          color: white;
          padding: 15px 40px;
          display: flex;
          align-items: center;
          justify-content: space-between;
          box-shadow: 0 2px 5px rgba(0,0,0,0.1);
          z-index: 999;
        }

        .logo {
          font-weight: 700;
          font-size: 1.5rem;
          letter-spacing: 1px;
        }

        .nav-links {
          display: flex;
          gap: 30px;
        }

        .nav-links a {
          color: white;
          text-decoration: none;
          font-weight: 600;
          font-size: 1.1rem;
          padding-bottom: 4px;
          border-bottom: 3px solid transparent;
          transition: border-color 0.3s ease, color 0.3s ease;
          cursor: pointer;
        }

        .nav-links a:hover {
          color: #cce4ff;
          border-bottom-color: #cce4ff;
        }

        .nav-links a.active {
          border-bottom-color: white;
          font-weight: 700;
        }

        .page-content {
          max-width: 900px;
          margin: 100px auto 60px; /* leave space for fixed header */
          background: white;
          border-radius: 8px;
          box-shadow: 0 6px 18px rgba(0, 0, 0, 0.1);
          padding: 40px 30px;
          box-sizing: border-box;
        }

        .section {
          margin-bottom: 60px;
          padding-bottom: 20px;
          border-bottom: 1px solid #e0e0e0;
        }

        .section:last-child {
          margin-bottom: 0;
          border-bottom: none;
        }

        .section h2 {
          color: #0f62fe;
          font-weight: 700;
          font-size: 1.8rem;
          margin-bottom: 20px;
          border-bottom: 2px solid #0f62fe;
          padding-bottom: 8px;
        }

        .home-section p,
        .about-section p,
        .projects-section p,
        .contact-section p {
          font-size: 1.1rem;
          line-height: 1.6;
          color: #444;
        }

        .project-list {
          display: flex;
          flex-wrap: wrap;
          gap: 25px;
          justify-content: center;
        }

        .project-card {
          background: #fefefe;
          border-radius: 10px;
          box-shadow: 0 3px 12px rgba(0,0,0,0.07);
          padding: 25px;
          flex: 1 1 280px;
          max-width: 280px;
          transition: box-shadow 0.3s ease, transform 0.3s ease;
        }

        .project-card:hover {
          box-shadow: 0 8px 24px rgba(0,0,0,0.12);
          transform: translateY(-7px);
        }

        .project-card h3 {
          margin-top: 0;
          color: #0f62fe;
          font-size: 1.4rem;
        }

        .project-card p {
          color: #555;
          line-height: 1.5;
          margin-bottom: 15px;
        }

        .project-links a {
          color: #0f62fe;
          text-decoration: none;
          margin-right: 15px;
          font-weight: 600;
          font-size: 0.95rem;
        }

        .project-links a:hover {
          text-decoration: underline;
        }

        .contact-form label {
          display: block;
          font-weight: 600;
          margin-bottom: 8px;
          color: #333;
        }

        .contact-form input,
        .contact-form textarea {
          width: 100%;
          padding: 12px 15px;
          margin-bottom: 20px;
          border: 1.5px solid #ccc;
          border-radius: 6px;
          font-size: 16px;
          resize: vertical;
          transition: border-color 0.3s ease;
        }

        .contact-form input:focus,
        .contact-form textarea:focus {
          outline: none;
          border-color: #0f62fe;
          box-shadow: 0 0 6px rgba(15, 98, 254, 0.4);
        }

        .contact-form button {
          background-color: #0f62fe;
          color: white;
          border: none;
          padding: 14px 32px;
          font-size: 16px;
          font-weight: 700;
          border-radius: 7px;
          cursor: pointer;
          transition: background-color 0.3s ease;
        }

        .contact-form button:hover {
          background-color: #0046d5;
        }
      `}</style>
    </>
  );
}
